// SPDX-License-Identifier: MIT
pragma solidity ^0.8.16;

import "@chainlink/contracts/src/v0.8/vrf/VRFConsumerBaseV2.sol";
import "@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol";
import "hardhat/console.sol";
import "@chainlink/contracts/src/v0.8/automation/AutomationCompatible.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

error Raffle__NotEnoughEth();
error Raffle__TransferFailed();
error Raffle__NotOpen();
error Raffle__UpkeepNotNeeded(
	uint256 currentBalance,
	uint256 numPlayers,
	uint256 raffleState
);

contract Raffle is VRFConsumerBaseV2, AutomationCompatible, Ownable {
	/* state variables */
	uint256 private i_entranceFee;
	address payable[] private s_players;
	address payable public treasury;
	address[] public specialWallets;

	/** Lottey Variables */
	address private s_recentWinner;
	enum RaffleState {
		OPEN,
		CALCULATING
	}

	RaffleState private s_raffleState;
	uint256 private s_lastTimeStamp;
	uint256 private immutable i_interval;

	/* Chainlink VRF Variables */
	VRFCoordinatorV2Interface private immutable i_vrfCOORDINATOR;
	bytes32 private immutable i_gasLane;
	uint64 private immutable i_subscriptionId;
	uint16 private constant REQUEST_CONFIRMATIONS = 3;
	uint32 private immutable i_callbackGasLimit;
	uint32 private constant NUM_WORDS = 3;

	/* events */
	event RaffleEnter(address indexed player);
	event RequestedRaffleWinnerFirst(uint256 indexed requestId);
	event RequestedRaffleWinnerSecond(uint256 indexed requestId);
	event RequestedRaffleWinnerThird(uint256 indexed requestId);
	event FirstWinnerPicked(address indexed winner);
	event SecondWinnerPicked(address indexed winner);
	event ThirdWinnerPicked(address indexed winner);
	event TreasuryChanged(address indexed newTreasury);

	constructor(
		address VRFCoordinatorV2,
		uint256 entranceFee,
		bytes32 keyHash,
		uint64 subscriptionId,
		uint32 callBackGasLimit,
		uint256 interval,
		address payable _treasury
	) VRFConsumerBaseV2(VRFCoordinatorV2) {
		i_entranceFee = entranceFee;
		i_vrfCOORDINATOR = VRFCoordinatorV2Interface(VRFCoordinatorV2);
		i_gasLane = keyHash;
		i_subscriptionId = subscriptionId;
		i_callbackGasLimit = callBackGasLimit;
		s_raffleState = RaffleState.OPEN;
		s_lastTimeStamp = block.timestamp;
		i_interval = interval;
		treasury = _treasury;
	}

	function enterRaffle() public payable {
		require(s_raffleState == RaffleState.OPEN, "Raffle__NotOpen");

		bool isSpecialWallet = false;
		for (uint256 i = 0; i < specialWallets.length; i++) {
			if (msg.sender == specialWallets[i]) {
				isSpecialWallet = true;
				break;
			}
		}

		if (!isSpecialWallet && msg.value < i_entranceFee) {
			revert("Raffle__NotEnoughEth");
		}

		s_players.push(payable(msg.sender));

		emit RaffleEnter(msg.sender);
	}

	/**
	 * @dev This is the function that the Chainlink Keeper nodes call
	 * they look for `upkeepNeeded` to return True.
	 * the following should be true for this to return true:
	 * 1. The time interval has passed between raffle runs.
	 * 2. The lottery is open.
	 * 3. The contract has ETH.
	 * 4. Implicity, your subscription is funded with LINK.
	 */
	/** CHAINLINK KEEPERS (AUTOMATION) */
	function checkUpkeep(
		bytes memory /* checkData*/
	)
		public
		view
		override
		returns (
			// external was changed to public so our own functions can call this function */
			bool upkeepNeeded,
			bytes memory /* performData*/
		)
	{
		bool isOpen = RaffleState.OPEN == s_raffleState;
		bool hasBalance = address(this).balance > 0;
		bool hasPlayers = s_players.length > 3;
		bool timePassed = ((block.timestamp - s_lastTimeStamp) > i_interval);

		upkeepNeeded = (timePassed && isOpen && hasBalance && hasPlayers);
		return (upkeepNeeded, "0x0");
	}

	/**
	 * @dev Once `checkUpkeep` is returning `true`, this function is called
	 * and it kicks off a Chainlink VRF call to get a random winner.
	 */
	function performUpkeep(bytes calldata /* performData */) external override {
		(bool upKeepNeeded, ) = checkUpkeep("");
		if (!upKeepNeeded) {
			revert Raffle__UpkeepNotNeeded(
				address(this).balance,
				s_players.length,
				uint256(s_raffleState)
			);
		}

		s_raffleState = RaffleState.CALCULATING;

		uint256 requestId = i_vrfCOORDINATOR.requestRandomWords(
			i_gasLane,
			i_subscriptionId,
			REQUEST_CONFIRMATIONS,
			i_callbackGasLimit,
			NUM_WORDS
		);
		emit RequestedRaffleWinnerFirst(requestId);
		emit RequestedRaffleWinnerSecond(requestId);
		emit RequestedRaffleWinnerThird(requestId);
	}

	/** CHAINLINK VRF */
	function fulfillRandomWords(
		uint256 /* requestId */,
		uint256[] memory randomWords
	) internal override {
		require(s_players.length >= 3, "Not enough players");
		// Calculate winnings based on the contract's balance
		uint256 totalBalance = address(this).balance;
		uint256 firstPrize = (totalBalance * 35) / 100;
		uint256 secondPrize = (totalBalance * 20) / 100;
		uint256 thirdPrize = (totalBalance * 5) / 100;
		uint256 treasuryAmount = (totalBalance * 10) / 100;

		// Ensure unique winners - simplistic approach, consider edge cases for production
		address payable firstWinner = s_players[
			randomWords[0] % s_players.length
		];
		address payable secondWinner = s_players[
			randomWords[1] % s_players.length
		];
		while (secondWinner == firstWinner) {
			secondWinner = s_players[(randomWords[1] + 1) % s_players.length];
		}
		address payable thirdWinner = s_players[
			randomWords[2] % s_players.length
		];
		while (thirdWinner == firstWinner || thirdWinner == secondWinner) {
			thirdWinner = s_players[(randomWords[2] + 1) % s_players.length];
		}

		// Transfer winnings to the winners
		(bool successFirst, ) = firstWinner.call{ value: firstPrize }("");
		require(successFirst, "Transfer to first winner failed");

		(bool successSecond, ) = secondWinner.call{ value: secondPrize }("");
		require(successSecond, "Transfer to second winner failed");

		(bool successThird, ) = thirdWinner.call{ value: thirdPrize }("");
		require(successThird, "Transfer to third winner failed");

		// Transfer the treasury's share
		(bool successTreasury, ) = treasury.call{ value: treasuryAmount }("");
		require(successTreasury, "Transfer to treasury failed");

		// Reset for next raffle
		s_recentWinner = firstWinner; // Consider how you want to handle this with multiple winners
		s_raffleState = RaffleState.OPEN;
		s_players = new address payable[](0);
		s_lastTimeStamp = block.timestamp;

		emit FirstWinnerPicked(firstWinner);
		emit SecondWinnerPicked(secondWinner);
		emit ThirdWinnerPicked(thirdWinner);
	}

	function getBalance() public view returns (uint256) {
		return address(this).balance;
	}

	function getRaffleState() public view returns (RaffleState) {
		return s_raffleState;
	}

	function getEntranceFee() public view returns (uint256) {
		return i_entranceFee;
	}

	function getPlayer(uint256 index) public view returns (address) {
		return s_players[index];
	}

	function getRecentWinner() public view returns (address) {
		return s_recentWinner;
	}

	function getLastTimeStamp() public view returns (uint256) {
		return s_lastTimeStamp;
	}

	function getInterval() public view returns (uint256) {
		return i_interval;
	}

	function getNumberOfPlayers() public view returns (uint256) {
		return s_players.length;
	}

	function addSpecialWallet(address _wallet) external onlyOwner {
		specialWallets.push(_wallet);
	}

	// Function to update the entrance fee
	function setEntranceFee(uint256 _newEntranceFee) external onlyOwner {
		i_entranceFee = _newEntranceFee;
	}

	function setTreasury(address payable _newTreasury) public onlyOwner {
		require(
			_newTreasury != address(0),
			"New treasury cannot be the zero address"
		);
		treasury = _newTreasury;
		emit TreasuryChanged(_newTreasury);
	}
}

